document.addEventListener('DOMContentLoaded', function() {
    const loginContainer = document.getElementById('login-container');
    const mainContainer = document.getElementById('main-container');
    const registerLink = document.getElementById('register-link');
    const loginForm = document.getElementById('login-form');
    const searchInput = document.getElementById('search-input');
    const searchButton = document.getElementById('search-button');
    const mobilesContainer = document.getElementById('mobiles-container');

    let loggedIn = false;
    let users = [];

    // Sample mobiles data
    const mobiles = [
        { name: 'iPhone 15 Pro Max', price: '$1499', rating: '4.8', description: 'Super Retina XDR display, A16 Bionic chip, Pro camera system with 12MP Ultra Wide, Wide, and Telephoto cameras; Night mode, Deep Fusion, Smart HDR 4, ProRAW, 4K Dolby Vision HDR recording', image: 'data:image/jpeg;base64,' },
        { name: 'OnePlus Nord CE', price: '$499', rating: '4.5', description: 'Fluid AMOLED display, Snapdragon 765G chip, Triple camera system with 64MP primary sensor, 5G connectivity, Warp Charge 30T', image: 'data:image/jpeg;base64,'},
        { name: 'iPhone 13 Pro', price: '$1099', rating: '4.7', description: 'Super Retina XDR display, A15 Bionic chip, Triple-camera system with 12MP Ultra Wide, Wide, and Telephoto cameras; Night mode, Deep Fusion, Smart HDR 4, ProRAW, 4K Dolby Vision HDR recording', image: 'data:image/jpeg;base64,' },
        { name: 'OnePlus 12R', price: '$799', rating: '4.4', description: 'Fluid AMOLED display, Snapdragon 888 chip, Dual-camera system with 48MP primary sensor, 5G connectivity, Warp Charge 65T', image: 'data:image/jpeg;base64,' },
        { name: 'Samsung Galaxy M55', price: '$499', rating: '4.5', description: 'Super AMOLED display, Exynos 1200 chip, Triple-camera system with 50MP primary sensor, 6000mAh battery, One UI 4.0', image: 'data:image/jpeg;base64,' },
        { name: 'Samsung Galaxy A55', price: '$599', rating: '4.6', description: 'Super AMOLED display, Exynos 1100 chip, Quad-camera system with 64MP primary sensor, 4500mAh battery, One UI 4.0', image: 'data:image/jpeg;base64,' },
        { name: 'Oppo A79', price: '$199', rating: '4.3', description: 'AMOLED display, MediaTek Helio P95 chip, Dual-camera system with 48MP primary sensor, 4000mAh battery, VOOC Flash Charge', image: 'data:image/jpeg;base64,' },
        { name: 'iPhone 11', price: '$699', rating: '4.6', description: 'Liquid Retina HD display, A13 Bionic chip, Dual-camera system with 12MP Ultra Wide and Wide cameras; Night mode, Portrait mode, 4K video recording', image: 'data:image/jpeg;base64,' },
    ];

    // Event listener for registering
    registerLink.addEventListener('click', function(event) {
        event.preventDefault();
        const newUsername = prompt('Enter a username:');
        const newPassword = prompt('Enter a password:');
        if (newUsername && newPassword) {
            users.push({ username: newUsername, password: newPassword });
            alert('Registration successful! Please login.');
        } else {
            alert('Username and password are required.');
        }
    });

    // Event listener for login
    loginForm.addEventListener('submit', function(event) {
        event.preventDefault();
        const username = loginForm.elements['username'].value;
        const password = loginForm.elements['password'].value;

        // Check if the user is registered
        const user = users.find(user => user.username === username && user.password === password);

        if (user) {
            loggedIn = true;
            loginContainer.classList.add('hidden');
            mainContainer.classList.remove('hidden');
            displayMobiles();
        } else {
            alert('Invalid username or password');
        }
    });

    // Function to display mobiles
    function displayMobiles(filteredMobiles = mobiles) {
        mobilesContainer.innerHTML = '';
        filteredMobiles.forEach(function(mobile) {
            const mobileDiv = document.createElement('div');
            mobileDiv.classList.add('mobile');

            const img = document.createElement('img');
            img.src = mobile.image;
            img.alt = mobile.name;

            const name = document.createElement('h3');
            name.textContent = mobile.name;

            const price = document.createElement('p');
            price.textContent = 'Price: ' + mobile.price;

            mobileDiv.appendChild(img);
            mobileDiv.appendChild(name);
            mobileDiv.appendChild(price);

            mobileDiv.addEventListener('click', function() {
                displayProductDetails(mobile);
            });

            mobilesContainer.appendChild(mobileDiv);
        });
    }

    // Function to display product details
    function displayProductDetails(product) {
        const productDetailsContainer = document.getElementById('product-details-container');
        const productName = document.getElementById('product-name');
        const productPrice = document.getElementById('product-price');
        const productRating = document.getElementById('product-rating');
        const productDescription = document.getElementById('product-description');
        const productImage = document.getElementById('product-image');

        productName.textContent = product.name;
        productPrice.textContent = 'Price: ' + product.price;
        productRating.textContent = 'Rating: ' + product.rating;
        productDescription.textContent = 'Description: ' + product.description;
        productImage.src = product.image;
        productImage.alt = product.name;

        // Hide main container and display product details container
        mainContainer.classList.add('hidden');
        productDetailsContainer.classList.remove('hidden');
    }

    // Event listener for search button
    searchButton.addEventListener('click', function() {
        const searchTerm = searchInput.value.trim().toLowerCase();
        const filteredMobiles = mobiles.filter(function(mobile) {
            return mobile.name.toLowerCase().includes(searchTerm);
        });
        displayMobiles(filteredMobiles);
    });
});
